import React, {Component, useState} from 'react';
import logo from './logo.svg';
import './App.css';
import VoteSystem from "./Components/Classwork02 VotingSystem/VotingSystem";


class App extends Component {
    state = {
        smthYouHate: 'corona virus',
        msgText: 'is suffering',
    };

    deleteVirusHandler = (event) =>{
       this.setState({
           msgText: 'deleting is forbidden by the definition of life'
           });
    };

    changeInputValueHandler = (event)=> {
        this.setState({
            smthYouHate: event.target.value
            }

        )
        console.log(event);

    };

    render() {
        const message = `${this.state.smthYouHate} ${this.state.msgText}`;
        return (
            <>
                <input type='text' onChange={this.changeInputValueHandler} value={this.state.smthYouHate}/>
                <h1 className='title'>{message}</h1>
                <button
                    onClick = {this.deleteVirusHandler}>
                    Delete {this.state.smthYouHate} from your life
                </button>
            </>
        );
    }
}
export default App;
